export const userReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN":
    case "REGISTER":
      return { ...state, isLogged: true, username: action.username };
    case "LOGOUT":
      return { ...state, isLogged: false };
    default:
      return state;
  }
};
