import './App.css';
import React, { useState, useReducer } from 'react';
import Authentication from './components/Authentication';
import TodoPage from './components/TodoPage';
import { userReducer } from './reducer/UserReducer';

function App() {
  const [state, dispatchUser] = useReducer(userReducer, { isLogged: false, username: "" });
  const [userList, setUserList] = useState([{ username: "henry", password: "123" }]);
  return (
    <>
      {
        !state.isLogged ?
          <Authentication dispatchUser={dispatchUser} userList={userList} setUserList={setUserList} />
          : <TodoPage username={state.username} dispatchUser={dispatchUser}  />
      }
    </>

  );
}

export default App;
